document.querySelector(".search-form").addEventListener("submit",function(){document.getElementById("loading").classList.add("show")});document.querySelectorAll(".detail-item, .info-card").forEach(e=>{e.addEventListener("mouseenter",function(){this.style.transform="translateY(-8px) scale(1.02)"}),e.addEventListener("mouseleave",function(){this.style.transform="translateY(0) scale(1)"})});
